const activo = true;

let mensaje = '';

//Forma tradicional de la condicional
if(activo){
    mensaje = 'Activo';
}else{
    mensaje = 'Inactivo';
}

console.log(mensaje);


//usando el operador ternario
let msg = (!activo) ? 'Activo' : 'Inactivo';
//let msg = (!activo) ? 'Activo' : null;
console.log(msg);

const mensaje2 = !activo && 'Activo';
console.log(mensaje2);
